mapper.select:
select count(*) as count from BASE_CROSS where 1=1

    AND TOKEN_VEH_BUREAU= #{tokenVehBureau}


    AND START_BUREAU = #{startBureau}


    AND HIGHLINE_FLAG = #{highlineFlag}


    AND check_time is null


    AND check_time is not null


    AND CREAT_UNIT_TIME is null


    AND cross_name like '%' || #{trainNbr} || '%'


    AND CREAT_UNIT_TIME is not null

select * from (select ROWNUM AS num, m.* from (select BASE_CHART_ID as chartId, BASE_CROSS_ID as crossId,CROSS_NAME as crossName,TOKEN_VEH_BUREAU as tokenVehBureau ,START_BUREAU as startBureau,HIGHLINE_FLAG as highlineFlag,creat_time as createTime, case when CREAT_UNIT_TIME is not null then '1' else '0' end as unitCreateFlag, case when check_time is not null then '1' else '0' end as checkFlag from BASE_CROSS where 1=1
AND BASE_CHART_ID= #{chartId}
AND TOKEN_VEH_BUREAU= #{tokenVehBureau}
AND cross_name like '%' || #{trainNbr} || '%'
AND START_BUREAU = #{startBureau}
AND HIGHLINE_FLAG = #{highlineFlag}
AND check_time is null
AND check_time is not null
AND CREAT_UNIT_TIME is null
AND CREAT_UNIT_TIME is not null
order by crossName ) m ) where num >=#{rownumstart} AND num <=#{rownumend}

select count(*) as  count from UNIT_CROSS where 1=1

    AND TOKEN_VEH_BUREAU= #{tokenVehBureau}


    AND BASE_CHART_ID= #{chartId}


    AND START_BUREAU = #{startBureau}


    AND HIGHLINE_FLAG = #{highlineFlag}


    AND check_time is null


    AND CROSS_NAME like '%' || #{trainNbr} || '%'


    AND check_time is not null


    AND CREAT_CROSS_TIME is null


    AND CREAT_CROSS_TIME is not null

select * from (select ROWNUM AS num,m.* from (select UNIT_CROSS_ID as unitCrossId, TOKEN_VEH_BUREAU as tokenVehBureau , START_BUREAU as startBureau, HIGHLINE_FLAG as highlineFlag, CROSS_NAME as crossName, BASE_CROSS_ID as crossId, case when check_time is not null then '1' else '0' end as checkFlag, case when CREAT_CROSS_TIME is not null then '1' else '0' end as unitCreateFlag from UNIT_CROSS where 1=1
AND TOKEN_VEH_BUREAU= #{tokenVehBureau}
AND START_BUREAU = #{startBureau}
AND BASE_CHART_ID= #{chartId}
AND HIGHLINE_FLAG = #{highlineFlag}
AND CROSS_NAME like '%' || #{trainNbr} || '%'
AND check_time is null
AND check_time is not null
AND CREAT_CROSS_TIME is null
AND CREAT_CROSS_TIME is not null
order by CROSS_NAME) m ) where num >=#{rownumstart} AND num <=#{rownumend}
